        }

        (len, byteIdx) = rawResp.readFixedArray(byteIdx);
        ret.new_allocations = new uint64[](len);

        for (uint i = 0; i < len; i++) {
            (ret.new_allocations[i], byteIdx) = rawResp.readUInt64(byteIdx);
        }
