        uint64 index;
        int8 skipped;
    }

    struct PoStProof {
        RegisteredPoStProof post_proof;
        bytes proof_bytes;
    }

    struct VestingFunds {
        int64 epoch;
        BigInt amount;
    }
    struct SectorDeals {
        int64 sector_type;
        int64 sector_expiry;
        uint64[] deal_ids;
    }

